import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const App = () => {
  // Navigation state
  const [currentView, setCurrentView] = useState('home'); // home, exercise, profile, settings
  const [currentExercise, setCurrentExercise] = useState(null);
  const [isActive, setIsActive] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);
  const [breathPhase, setBreathPhase] = useState('inhale');
  const [phaseTime, setPhaseTime] = useState(0);

  // Enhanced metrics
  const [metrics, setMetrics] = useState({
    totalSessions: 0,
    totalMinutes: 0,
    streak: 0,
    lastSession: null,
    favoriteExercises: [],
    weeklyGoal: 5,
    weeklyProgress: 0,
    benefits: {
      stressReduction: 0,
      focusImprovement: 0,
      sleepQuality: 0,
      anxietyRelief: 0
    }
  });

  // User profile and preferences
  const [profile, setProfile] = useState({
    name: '',
    mindfulnessGoals: [],
    preferredDuration: 'short', // short, medium, long
    soundEnabled: true,
    hapticEnabled: true,
    reminderTime: null,
    experienceLevel: 'beginner' // beginner, intermediate, advanced
  });

  // Audio system
  const [audioEnabled, setAudioEnabled] = useState(true);
  const audioRef = useRef(null);
  const backgroundAudioRef = useRef(null);

  const intervalRef = useRef(null);
  const phaseIntervalRef = useRef(null);

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedMetrics = localStorage.getItem('breathworkMetrics');
    const savedProfile = localStorage.getItem('breathworkProfile');

    if (savedMetrics) {
      setMetrics(JSON.parse(savedMetrics));
    }
    if (savedProfile) {
      setProfile(JSON.parse(savedProfile));
    }
  }, []);

  // Save data to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('breathworkMetrics', JSON.stringify(metrics));
  }, [metrics]);

  useEffect(() => {
    localStorage.setItem('breathworkProfile', JSON.stringify(profile));
  }, [profile]);

  // Audio system
  useEffect(() => {
    if (audioEnabled && profile.soundEnabled) {
      // Create audio context for breathing sounds
      if (!audioRef.current) {
        audioRef.current = new (window.AudioContext || window.webkitAudioContext)();
      }
    }
  }, [audioEnabled, profile.soundEnabled]);

  // Play breathing sound
  const playBreathingSound = (phase) => {
    if (!audioEnabled || !profile.soundEnabled) return;

    try {
      const oscillator = audioRef.current.createOscillator();
      const gainNode = audioRef.current.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(audioRef.current.destination);

      // Different frequencies for different phases
      const frequencies = {
        inhale: 220,
        hold: 330,
        exhale: 110,
        notice: 440
      };

      oscillator.frequency.setValueAtTime(frequencies[phase] || 220, audioRef.current.currentTime);
      oscillator.type = 'sine';

      gainNode.gain.setValueAtTime(0, audioRef.current.currentTime);
      gainNode.gain.linearRampToValueAtTime(0.1, audioRef.current.currentTime + 0.1);
      gainNode.gain.linearRampToValueAtTime(0, audioRef.current.currentTime + 0.5);

      oscillator.start(audioRef.current.currentTime);
      oscillator.stop(audioRef.current.currentTime + 0.5);
    } catch (error) {
      console.log('Audio not available:', error);
    }
  };

  // Mobile haptic feedback
  const triggerHapticFeedback = () => {
    if (profile.hapticEnabled && 'vibrate' in navigator) {
      navigator.vibrate([50, 30, 50]);
    }
  };

  // Enhanced exercise configurations
  const exercises = {
    boxBreathing: {
      name: 'Box Breathing',
      duration: 20,
      difficulty: 'beginner',
      category: 'calming',
      phases: [
        { name: 'Inhale', duration: 4, instruction: 'Breathe in slowly through your nose' },
        { name: 'Hold', duration: 4, instruction: 'Hold your breath gently' },
        { name: 'Exhale', duration: 4, instruction: 'Exhale slowly through your mouth' },
        { name: 'Hold', duration: 4, instruction: 'Hold empty, then repeat' }
      ],
      description: 'A simple 4-4-4-4 breathing pattern to calm your nervous system',
      benefits: ['Reduces stress', 'Improves focus', 'Calms anxiety']
    },
    deepBelly: {
      name: 'Deep Belly Breath',
      duration: 15,
      difficulty: 'beginner',
      category: 'calming',
      phases: [
        { name: 'Inhale', duration: 5, instruction: 'Breathe deeply into your belly' },
        { name: 'Exhale', duration: 10, instruction: 'Exhale slowly and completely' }
      ],
      description: 'Deep diaphragmatic breathing to activate your parasympathetic nervous system',
      benefits: ['Reduces stress', 'Improves sleep', 'Calms anxiety']
    },
    mindfulPause: {
      name: 'Mindful Pause',
      duration: 30,
      difficulty: 'beginner',
      category: 'grounding',
      phases: [
        { name: 'Notice', duration: 30, instruction: 'Notice 5 things you see, 4 you feel, 3 sounds, 2 smells, 1 taste' }
      ],
      description: 'A grounding technique using your five senses to bring you back to the present moment',
      benefits: ['Reduces anxiety', 'Improves focus', 'Grounds you in the present']
    },
    energizing: {
      name: 'Energizing Breath',
      duration: 25,
      difficulty: 'intermediate',
      category: 'energizing',
      phases: [
        { name: 'Inhale', duration: 3, instruction: 'Quick, energizing inhale' },
        { name: 'Exhale', duration: 3, instruction: 'Quick, powerful exhale' },
        { name: 'Hold', duration: 2, instruction: 'Brief pause' }
      ],
      description: 'Quick breathing pattern to increase energy and alertness',
      benefits: ['Increases energy', 'Improves focus', 'Boosts alertness']
    },
    sleepPrep: {
      name: 'Sleep Preparation',
      duration: 40,
      difficulty: 'beginner',
      category: 'sleep',
      phases: [
        { name: 'Inhale', duration: 6, instruction: 'Slow, deep inhale' },
        { name: 'Exhale', duration: 8, instruction: 'Longer, relaxing exhale' }
      ],
      description: 'Slow breathing pattern to prepare your body and mind for sleep',
      benefits: ['Improves sleep', 'Reduces stress', 'Calms the mind']
    }
  };

  // Start exercise
  const startExercise = (exerciseKey) => {
    const exercise = exercises[exerciseKey];
    setCurrentExercise({ key: exerciseKey, ...exercise });
    setTimeLeft(exercise.duration);
    setIsActive(true);
    setBreathPhase('inhale');
    setPhaseTime(0);
    setCurrentView('exercise');

    // Update metrics
    setMetrics(prev => ({
      ...prev,
      totalSessions: prev.totalSessions + 1,
      totalMinutes: prev.totalMinutes + exercise.duration,
      lastSession: new Date().toISOString()
    }));

    triggerHapticFeedback();
    playBreathingSound('inhale');
  };

  // Stop exercise
  const stopExercise = () => {
    setIsActive(false);
    setCurrentExercise(null);
    setTimeLeft(0);
    setBreathPhase('inhale');
    setPhaseTime(0);
    setCurrentView('home');

    if (intervalRef.current) clearInterval(intervalRef.current);
    if (phaseIntervalRef.current) clearInterval(phaseIntervalRef.current);
  };

  // Timer countdown
  useEffect(() => {
    if (isActive && timeLeft > 0) {
      intervalRef.current = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setIsActive(false);
            setCurrentExercise(null);
            setCurrentView('home');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (intervalRef.current) clearInterval(intervalRef.current);
    }

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [isActive, timeLeft]);

  // Breathing phase management
  useEffect(() => {
    if (isActive && currentExercise) {
      const currentPhase = currentExercise.phases[0];
      let phaseIndex = 0;
      let phaseStartTime = Date.now();

      phaseIntervalRef.current = setInterval(() => {
        const elapsed = Date.now() - phaseStartTime;
        const currentPhaseDuration = currentPhase.duration * 1000;

        if (elapsed >= currentPhaseDuration) {
          phaseIndex = (phaseIndex + 1) % currentExercise.phases.length;
          phaseStartTime = Date.now();
          const newPhase = currentExercise.phases[phaseIndex].name.toLowerCase().replace(' ', '');
          setBreathPhase(newPhase);
          playBreathingSound(newPhase);
        }
      }, 100);

      return () => {
        if (phaseIntervalRef.current) clearInterval(phaseIntervalRef.current);
      };
    }
  }, [isActive, currentExercise]);

  // Mark benefit
  const markBenefit = (benefitType) => {
    setMetrics(prev => ({
      ...prev,
      benefits: {
        ...prev.benefits,
        [benefitType]: prev.benefits[benefitType] + 1
      }
    }));
    triggerHapticFeedback();
  };

  // Save exercise as favorite
  const toggleFavorite = (exerciseKey) => {
    setMetrics(prev => ({
      ...prev,
      favoriteExercises: prev.favoriteExercises.includes(exerciseKey)
        ? prev.favoriteExercises.filter(key => key !== exerciseKey)
        : [...prev.favoriteExercises, exerciseKey]
    }));
  };

  // Update profile
  const updateProfile = (updates) => {
    setProfile(prev => ({ ...prev, ...updates }));
  };

  // Format time display
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Navigation component
  const Navigation = () => (
    <nav className="navigation">
      <button 
        className="nav-btn" 
        onClick={() => setCurrentView('home')}
        disabled={currentView === 'home'}
      >
        üè† Home
      </button>
      <button 
        className="nav-btn" 
        onClick={() => setCurrentView('profile')}
        disabled={currentView === 'profile'}
      >
        üë§ Profile
      </button>
      <button 
        className="nav-btn" 
        onClick={() => setCurrentView('settings')}
        disabled={currentView === 'settings'}
      >
        ‚öôÔ∏è Settings
      </button>
    </nav>
  );

  // Home view
  const HomeView = () => (
    <div className="home-view">
      <div className="welcome-section">
        <h2>Welcome back{profile.name ? `, ${profile.name}` : ''}</h2>
        <p>Take a moment to breathe and center yourself</p>
      </div>

      <div className="metrics-overview">
        <div className="metric-card">
          <h3>{metrics.totalSessions}</h3>
          <p>Total Sessions</p>
        </div>
        <div className="metric-card">
          <h3>{metrics.totalMinutes}</h3>
          <p>Minutes Meditated</p>
        </div>
        <div className="metric-card">
          <h3>{metrics.streak}</h3>
          <p>Day Streak</p>
        </div>
      </div>

      <div className="exercises-section">
        <h3>Choose Your Practice</h3>
        <div className="exercise-cards">
          {Object.entries(exercises).map(([key, exercise]) => (
            <div 
              key={key}
              className={`exercise-card ${metrics.favoriteExercises.includes(key) ? 'favorite' : ''}`}
              onClick={() => startExercise(key)}
            >
              <div className="exercise-header">
                <h4>{exercise.name}</h4>
                <button 
                  className="favorite-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleFavorite(key);
                  }}
                >
                  {metrics.favoriteExercises.includes(key) ? '‚ù§Ô∏è' : 'ü§ç'}
                </button>
              </div>
              <p>{exercise.description}</p>
              <div className="exercise-meta">
                <span className="duration">{exercise.duration}s</span>
                <span className="difficulty">{exercise.difficulty}</span>
                <span className="category">{exercise.category}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Profile view
  const ProfileView = () => (
    <div className="profile-view">
      <h2>Your Profile</h2>

      <div className="profile-section">
        <label>Name</label>
        <input 
          type="text" 
          value={profile.name}
          onChange={(e) => updateProfile({ name: e.target.value })}
          placeholder="Enter your name"
        />
      </div>

      <div className="profile-section">
        <label>Experience Level</label>
        <select 
          value={profile.experienceLevel}
          onChange={(e) => updateProfile({ experienceLevel: e.target.value })}
        >
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
      </div>

      <div className="profile-section">
        <label>Preferred Duration</label>
        <select 
          value={profile.preferredDuration}
          onChange={(e) => updateProfile({ preferredDuration: e.target.value })}
        >
          <option value="short">Short (5-15 min)</option>
          <option value="medium">Medium (15-30 min)</option>
          <option value="long">Long (30+ min)</option>
        </select>
      </div>

      <div className="profile-section">
        <label>
          <input 
            type="checkbox" 
            checked={profile.soundEnabled}
            onChange={(e) => updateProfile({ soundEnabled: e.target.checked })}
          />
          Enable Sound
        </label>
      </div>

      <div className="profile-section">
        <label>
          <input 
            type="checkbox" 
            checked={profile.hapticEnabled}
            onChange={(e) => updateProfile({ hapticEnabled: e.target.checked })}
          />
          Enable Haptic Feedback
        </label>
      </div>

      <div className="benefits-section">
        <h3>Your Benefits</h3>
        <div className="benefits-grid">
          {Object.entries(metrics.benefits).map(([key, value]) => (
            <div key={key} className="benefit-item">
              <span className="benefit-label">{key.replace(/([A-Z])/g, ' $1').trim()}</span>
              <span className="benefit-value">{value}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Settings view
  const SettingsView = () => (
    <div className="settings-view">
      <h2>Settings</h2>

      <div className="settings-section">
        <h3>Audio</h3>
        <label>
          <input 
            type="checkbox" 
            checked={audioEnabled}
            onChange={(e) => setAudioEnabled(e.target.checked)}
          />
          Enable Audio Cues
        </label>
      </div>

      <div className="settings-section">
        <h3>Weekly Goal</h3>
        <input 
          type="number" 
          value={metrics.weeklyGoal}
          onChange={(e) => setMetrics(prev => ({ ...prev, weeklyGoal: parseInt(e.target.value) }))}
          min="1"
          max="20"
        />
        <p>Current progress: {metrics.weeklyProgress}/{metrics.weeklyGoal} sessions</p>
      </div>

      <div className="settings-section">
        <button 
          className="reset-btn"
          onClick={() => {
            if (window.confirm('Are you sure you want to reset all data?')) {
              setMetrics({
                totalSessions: 0,
                totalMinutes: 0,
                streak: 0,
                lastSession: null,
                favoriteExercises: [],
                weeklyGoal: 5,
                weeklyProgress: 0,
                benefits: {
                  stressReduction: 0,
                  focusImprovement: 0,
                  sleepQuality: 0,
                  anxietyRelief: 0
                }
              });
            }
