// Soothing Color Palette for Mental Wellness
export const colors = {
  // Primary soothing colors
  softBlue: '#6B9BD1',
  forestGreen: '#7FB069',
  mutedPurple: '#9B8FB8',
  charcoal: '#2C3E50',

  // Backgrounds
  backgroundLight: '#F8FAFC',
  backgroundSoft: '#F0F4F8',
  backgroundCard: '#FFFFFF',

  // Text
  textPrimary: '#1A202C',
  textSecondary: '#4A5568',
  textTertiary: '#718096',

  // Accents
  accentBlue: '#5A8FC7',
  accentGreen: '#6FA055',
  accentPurple: '#8A7BA5',
  accentOrange: '#E8A87C',

  // States
  success: '#48BB78',
  warning: '#ED8936',
  error: '#F56565',

  // Gradients
  gradientCalm: 'linear-gradient(135deg, #6B9BD1 0%, #9B8FB8 100%)',
  gradientEnergy: 'linear-gradient(135deg, #E8A87C 0%, #F4A460 100%)',
  gradientGround: 'linear-gradient(135deg, #7FB069 0%, #6B9BD1 100%)',

  // Shadows
  shadowSoft: '0 2px 8px rgba(0, 0, 0, 0.04)',
  shadowMedium: '0 4px 16px rgba(0, 0, 0, 0.08)',
  shadowLarge: '0 8px 32px rgba(0, 0, 0, 0.12)',
};

// Typography
export const typography = {
  fontFamily: '-apple-system, BlinkMacSystemFont, "SF Pro Display", "Inter", "Satoshi", "Segoe UI", sans-serif',
  fontSizes: {
    xs: '0.75rem',    // 12px
    sm: '0.875rem',   // 14px
    base: '1rem',     // 16px
    lg: '1.125rem',   // 18px
    xl: '1.25rem',    // 20px
    '2xl': '1.5rem',  // 24px
    '3xl': '1.875rem', // 30px
    '4xl': '2.25rem',  // 36px
    '5xl': '3rem',     // 48px
  },
  fontWeights: {
    light: 300,
    regular: 400,
    medium: 500,
    semibold: 600,
    bold: 700,
  },
  lineHeights: {
    tight: 1.2,
    normal: 1.5,
    relaxed: 1.75,
  },
};

// Spacing
export const spacing = {
  xs: '0.5rem',   // 8px
  sm: '0.75rem',  // 12px
  md: '1rem',     // 16px
  lg: '1.5rem',   // 24px
  xl: '2rem',     // 32px
  '2xl': '3rem',  // 48px
  '3xl': '4rem',  // 64px
};

// Border radius
export const borderRadius = {
  sm: '0.5rem',   // 8px
  md: '1rem',     // 16px
  lg: '1.5rem',   // 24px
  xl: '2rem',     // 32px
  full: '9999px',
};

// Transitions
export const transitions = {
  fast: '0.15s ease',
  normal: '0.3s ease',
  slow: '0.5s ease',
  breathing: '0.8s ease-in-out',
};
